 * repository methods below.
 */
class DomaineRepository extends \Doctrine\ORM\EntityRepository
{
    /**
     * liste ordonnÃ©
     */
    public function liste()
    {
        return $this->createQueryBuilder('d')->orderBy('d.libelle', 'ASC');
    }
}
